carregarProjetosDestaque();async function carregarProjetosDestaque(){const a=document.getElementById("projetos-destaque");if(a){const b=await getProjetos(),c=b.slice(0,3);a.innerHTML="",c.forEach(b=>{const c=`
        <article class="card-projeto">
          <img src="${b.imagem_capa}" alt="Capa do projeto ${b.titulo}">
          <h3>${b.titulo}</h3>
          <p>${b.resumo}</p>
          <a href="/pages/projeto-detalhe.html?id=${b.id}">Ver Mais</a>
        </article>
      `;a.innerHTML+=c})}}